mapper.update:
UPDATE S_J0a0J.board
SET has_file = 'Y'
where board_seq = #{boardSeq}

mapper.select:
SELECT board_seq, member_nick, title, content, has_file, hits, create_dtm, update_dtm, type_seq
FROM S_J0a0J.board
WHERE board_seq = ${boardSeq};

mapper.update:
UPDATE S_J0a0J.board
SET hits= hits + 1
WHERE board_seq=#{boardSeq};

UPDATE S_J0a0J.board
SET title=#{title}, content=#{content}, update_dtm=DATE_FORMAT(NOW(), '%Y.%m.%d %H:%i')
WHERE board_seq=#{boardSeq};
